import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./Portfolio.css";
import Footer from "./Footer";
import Navbar from "./Navbar";

const Portfolio = () => {
  const { id } = useParams(); // Retrieve artist ID from URL
  const [artist, setArtist] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [timeSlots, setTimeSlots] = useState([]);

  // Mock artist data with all 10 artists
  const artistData = [
    {
      id: 1,
      name: "John Doe",
      skills: ["Portrait Drawing", "Pencil Art"],
      location: "Pune",
      profilePicture: "https://via.placeholder.com/150",
      portfolio: [
        "https://via.placeholder.com/300x200/FFB6C1/000000?text=Art+1",
        "https://via.placeholder.com/300x200/ADD8E6/000000?text=Art+2",
      ],
      availability: {
        "2024-12-22": ["10:00 AM", "1:00 PM", "2:00 PM"], // Available slots
        "2024-12-23": ["9:00 AM", "11:00 AM", "4:00 PM"],
      },
    },
    {
      id: 2,
      name: "Jane Smith",
      skills: ["Modern Canvas Art", "Acrylic Painting"],
      location: "Mumbai",
      profilePicture: "https://via.placeholder.com/150",
      portfolio: [
        "https://via.placeholder.com/300x200/FFD700/000000?text=Canvas+1",
        "https://via.placeholder.com/300x200/98FB98/000000?text=Canvas+2",
      ],
      availability: {
        "2024-12-22": ["9:00 AM", "10:00 AM", "3:00 PM"],
        "2024-12-24": ["12:00 PM", "2:00 PM", "5:00 PM"],
      },
    },
    // Add other artists similarly...
  ];

  useEffect(() => {
    // Find artist by ID
    const selectedArtist = artistData.find((item) => item.id === parseInt(id));
    setArtist(selectedArtist);
  }, [id]);

  const handleDateClick = (date) => {
    setSelectedDate(date);
    if (artist?.availability[date]) {
      setTimeSlots(artist.availability[date]);
    } else {
      setTimeSlots([]); // No slots available for the selected date
    }
  };

  if (!artist) {
    return (
      <div className="error-container">
        <h2>Artist Not Found</h2>
        <p>Sorry, the artist you are looking for does not exist.</p>
      </div>
    );
  }

  const generateCalendar = () => {
    const currentDate = new Date();
    const days = Array.from({ length: 14 }, (_, i) => {
      const date = new Date(currentDate);
      date.setDate(currentDate.getDate() + i);
      return date;
    });

    return days.map((date) => {
      const formattedDate = date.toISOString().split("T")[0];
      return (
        <div
          key={formattedDate}
          className={`calendar-date ${
            artist.availability[formattedDate] ? "available-date" : "unavailable-date"
          }`}
          onClick={() => handleDateClick(formattedDate)}
        >
          {formattedDate}
        </div>
      );
    });
  };

  return (
    <>
      <Navbar />
      <div className="portfolio-container">
        {/* Artist Profile Section */}
        <div className="portfolio-header">
          <img
            src={artist.profilePicture}
            alt={artist.name}
            className="artist-profile-picture"
          />
          <div className="portfolio-details">
            <h2>{artist.name}</h2>
            <p>
              <strong>Skills:</strong> {artist.skills.join(", ")}
            </p>
            <p>
              <strong>Location:</strong> {artist.location}
            </p>
          </div>
        </div>

        {/* Portfolio Section */}
        <div className="portfolio-gallery-section">
          <h3>Portfolio</h3>
          <div className="portfolio-gallery">
            {artist.portfolio.map((item, index) => (
              <img
                key={index}
                src={item}
                alt={`Art ${index + 1}`}
                className="portfolio-image"
              />
            ))}
          </div>
        </div>

        {/* Calendar Section */}
        <div className="calendar-container">
          <h3>Availability Calendar</h3>
          <div className="calendar">{generateCalendar()}</div>
          {selectedDate && (
            <div className="time-slots">
              <h4>Time Slots for {selectedDate}</h4>
              {timeSlots.length > 0 ? (
                timeSlots.map((slot, index) => (
                  <div key={index} className="time-slot available-slot">
                    {slot}
                  </div>
                ))
              ) : (
                <div className="time-slot unavailable-slot">No Slots Available</div>
              )}
            </div>
          )}
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Portfolio;
