// src/Login.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Login.css';
import Footer from './Footer';
import Navbar from './Navbar';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    // Dummy data for users
    const dummyUsers = [
        { role: 'user', email: 'user@example.com', password: 'user123', redirect: '/user-dashboard' },
        { role: 'artist', email: 'artist@example.com', password: 'artist123', redirect: '/artist-dashboard' },
        { role: 'admin', email: 'admin@example.com', password: 'admin123', redirect: '/admin-dashboard' }
    ];

    const handleSubmit = (e) => {
        e.preventDefault();

        // Check if email and password match any dummy user
        const foundUser = dummyUsers.find(
            (user) => user.email === email && user.password === password
        );

        if (foundUser) {
            console.log(`Login successful as ${foundUser.role}`);
            alert(`Welcome, ${foundUser.role}!`);
            navigate(foundUser.redirect); // Navigate based on role
        } else {
            alert('Invalid email or password');
        }
    };

    return (
        <>
            <Navbar />
            <div className="login-container">
                <h2>Login</h2>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>Email:</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>
                <p>
                    Don't have an account?{' '}
                    <Link to="/register">Register here</Link>
                </p>
            </div>
            <Footer />
        </>
    );
};

export default Login;
