// src/ServiceArtists.js
import React from 'react';
import { useParams, Link } from 'react-router-dom';
import './ServiceArtists.css'; // Import the CSS file for styling

// Mock data for services and artists
const servicesData = [
  { service_id: 1, service_name: 'Portrait Drawing' },
  { service_id: 2, service_name: 'Modern Canvas Art' },
  { service_id: 3, service_name: 'Bridal Mehendi' },
  { service_id: 4, service_name: 'T-Shirt Printing' },
  { service_id: 5, service_name: 'Embroidery' },
  { service_id: 6, service_name: 'Chocolate Making' },
  { service_id: 7, service_name: 'Canvas Painting' },
  { service_id: 8, service_name: 'Digital Art' },
  { service_id: 9, service_name: 'Mehendi Service' },
  { service_id: 10, service_name: 'Handmade Crafts' },
];

const artistsData = [
  {
    id: 1,
    name: 'Aditi Shah',
    skills: ['Mehendi', 'Art', 'Design'],
    location: 'Pune',
    profilePicture: 'https://example.com/images/aditi.jpg',
  },
  {
    id: 2,
    name: 'Rajesh Kumar',
    skills: ['Painting', 'Mural', 'Canvas Art'],
    location: 'Mumbai',
    profilePicture: 'https://example.com/images/rajesh.jpg',
  },
  {
    id: 3,
    name: 'Nina Patel',
    skills: ['T-shirt Printing', 'Embroidery'],
    location: 'Mumbai',
    profilePicture: 'https://example.com/images/nina.jpg',
  },
  {
    id: 4,
    name: 'Sandeep Iyer',
    skills: ['Chocolate Art', 'Handcrafted Sweets'],
    location: 'Pune',
    profilePicture: 'https://example.com/images/sandeep.jpg',
  },
  {
    id: 5,
    name: 'Priya Sharma',
    skills: ['Mehendi', 'Design', 'Minimal Art'],
    location: 'Delhi',
    profilePicture: 'https://example.com/images/priya.jpg',
  },
  {
    id: 6,
    name: 'Amit Singh',
    skills: ['Event Planning', 'Wedding Coordination'],
    location: 'Bangalore',
    profilePicture: 'https://example.com/images/amit.jpg',
  },
  {
    id: 7,
    name: 'Tanvi Desai',
    skills: ['Photography', 'Wedding Photos', 'Event Photos'],
    location: 'Hyderabad',
    profilePicture: 'https://example.com/images/tanvi.jpg',
  },
  {
    id: 8,
    name: 'Karan Verma',
    skills: ['Calligraphy', 'Design', 'Invitations'],
    location: 'Chennai',
    profilePicture: 'https://example.com/images/karan.jpg',
  },
  {
    id: 9,
    name: 'Snehal Gupta',
    skills: ['Jewelry Design', 'Custom Pieces'],
    location: 'Kolkata',
    profilePicture: 'https://example.com/images/snehal.jpg',
  },
  {
    id: 10,
    name: 'Ravi Joshi',
    skills: ['Digital Art', 'Illustration', 'Character Design'],
    location: 'Surat',
    profilePicture: 'https://example.com/images/ravi.jpg',
  },
];

const ServiceArtists = () => {
  const { id } = useParams(); // Get service ID from URL params
  const serviceId = parseInt(id);

  // Get the service name for the current serviceId
  const service = servicesData.find((service) => service.service_id === serviceId);

  // Filter artists offering this service based on their skills
  const filteredArtists = artistsData.filter((artist) =>
    artist.skills.some((skill) => skill.toLowerCase().includes(service.service_name.toLowerCase()))
  );

  return (
    <div className="artists-container">
      <h1 className="artists-heading">
        {service ? `${service.service_name} - Artists` : 'Artists Offering This Service'}
      </h1>
      {filteredArtists.length > 0 ? (
        <div className="artists-list">
          {filteredArtists.map((artist) => (
            <div key={artist.id} className="artist-card">
              <img
                src={artist.profilePicture}
                alt={artist.name}
                className="artist-image"
              />
              <h3 className="artist-name">{artist.name}</h3>
              <p className="artist-location">{artist.location}</p>
              <Link to={`/portfolio/${artist.id}`}>
                <button className="view-profile-button">View Profile</button>
              </Link>
            </div>
          ))}
        </div>
      ) : (
        <p>No artists found for this service.</p>
      )}
    </div>
  );
};

export default ServiceArtists;
