// src/FindArtist.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './FindArtist.css'; // For styling

const FindArtist = () => {
  const [artists, setArtists] = useState([]);
  const navigate = useNavigate();

  // Load mock data (simulate API call)
  useEffect(() => {
    setArtists([
      {
        id: 1,
        name: 'John Doe',
        skills: ['Portrait Drawing', 'Pencil Art'],
        location: 'Pune',
        profilePicture: 'https://via.placeholder.com/150',
      },
      {
        id: 2,
        name: 'Jane Smith',
        skills: ['Modern Canvas Art', 'Acrylic Painting'],
        location: 'Mumbai',
        profilePicture: 'https://via.placeholder.com/150',
      },
      {
        id: 3,
        name: 'Alex Johnson',
        skills: ['Bridal Mehendi', 'Henna Art'],
        location: 'Delhi',
        profilePicture: 'https://via.placeholder.com/150',
      },
      {
        id: 4,
        name: 'Emma Brown',
        skills: ['T-Shirt Printing', 'Custom Designs'],
        location: 'Bangalore',
        profilePicture: 'https://via.placeholder.com/150',
      },
      {
        id: 5,
        name: 'Liam Wilson',
        skills: ['Embroidery', 'Hand-Stitched Designs'],
        location: 'Chennai',
        profilePicture: 'https://via.placeholder.com/150',
      },
    ]);
  }, []);

  // Handle navigation to the Portfolio page
  const handleViewProfile = (id) => {
    navigate(`/portfolio/${id}`);
  };

  return (
    <div className="artist-container">
      <h2 className="artist-heading">
        Find <span>Artists</span>
      </h2>
      <div className="artist-list">
        {artists.map((artist) => (
          <div key={artist.id} className="artist-card">
            <img src={artist.profilePicture} alt={artist.name} />
            <h3>{artist.name}</h3>
            <p>Skills: {artist.skills.join(', ')}</p>
            <p>Location: {artist.location}</p>
            <button onClick={() => handleViewProfile(artist.id)}>View Profile</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FindArtist;
