// ServiceArtists.js
import React from 'react';
import { useParams } from 'react-router-dom';
import './ServiceArtists.css'; // Import the CSS file for styling


// Mock data for artists
const artistsData = [
  { id: 1, name: 'John Doe', services: [1, 7], location: 'Pune' },
  { id: 2, name: 'Jane Smith', services: [2, 7], location: 'Mumbai' },
  { id: 3, name: 'Alex Johnson', services: [3, 9], location: 'Delhi' },
  { id: 4, name: 'Emma Brown', services: [4, 5], location: 'Bangalore' },
  { id: 5, name: 'Liam Wilson', services: [5], location: 'Chennai' },
  { id: 6, name: 'Olivia Taylor', services: [6], location: 'Hyderabad' },
  { id: 7, name: 'Noah Davis', services: [7], location: 'Kolkata' },
  { id: 8, name: 'Ava Thomas', services: [8], location: 'Pune' },
  { id: 9, name: 'Mason Moore', services: [9], location: 'Jaipur' },
  { id: 10, name: 'Mia White', services: [10], location: 'Surat' },
];

const ServiceArtists = () => {
  const { id } = useParams(); // Get service ID from URL params
  const serviceId = parseInt(id);

  // Filter artists offering this service
  const filteredArtists = artistsData.filter((artist) =>
    artist.services.includes(serviceId)
  );

  return (
    <div className="artists-container">
      <h1 className="artists-heading">Artists Offering This Service</h1>
      {filteredArtists.length > 0 ? (
        <ul className="artists-list">
          {filteredArtists.map((artist) => (
            <li key={artist.id} className="artist-item">
              {artist.name} - {artist.location}
            </li>
          ))}
        </ul>
      ) : (
        <p>No artists found for this service.</p>
      )}
    </div>
  );
};

export default ServiceArtists;
