import React from "react";
import "./Navbar.css";
import logo from './logo.png';
import { NavLink } from 'react-router-dom';

export default function Navbar({ role, onLogout }) {
    return (
        <div className="navbar">
            <div className="logo">
                <img src={logo} alt="Artistry-Hub Logo" />
            </div>
            <ul>
                {/* Common Links */}
                <li><NavLink to="/" className={({ isActive }) => isActive ? 'active' : ''}>Home</NavLink></li>
                <li><NavLink to="/blogs" className={({ isActive }) => isActive ? 'active' : ''}>Blogs</NavLink></li>
                <li><NavLink to="/about" className={({ isActive }) => isActive ? 'active' : ''}>About</NavLink></li>
                <li><NavLink to="/services" className={({ isActive }) => isActive ? 'active' : ''}>Services</NavLink></li>

                {/* Role-Specific Links */}
                {role === 'user' && (
                    <>
                        <li><NavLink to="/user-dashboard" className={({ isActive }) => isActive ? 'active' : ''}>User Dashboard</NavLink></li>
                        <li><NavLink to="/my-orders" className={({ isActive }) => isActive ? 'active' : ''}>My Orders</NavLink></li>
                    </>
                )}
                {role === 'artist' && (
                    <>
                        <li><NavLink to="/artist-dashboard" className={({ isActive }) => isActive ? 'active' : ''}>Artist Dashboard</NavLink></li>
                        <li><NavLink to="/my-services" className={({ isActive }) => isActive ? 'active' : ''}>My Services</NavLink></li>
                    </>
                )}
                {role === 'admin' && (
                    <>
                        <li><NavLink to="/admin-dashboard" className={({ isActive }) => isActive ? 'active' : ''}>Admin Dashboard</NavLink></li>
                        <li><NavLink to="/manage-users" className={({ isActive }) => isActive ? 'active' : ''}>Manage Users</NavLink></li>
                        <li><NavLink to="/manage-services" className={({ isActive }) => isActive ? 'active' : ''}>Manage Services</NavLink></li>
                    </>
                )}

                {/* Conditionally Render Login or Logout */}
                {role ? (
                    <li>
                        <NavLink to="/" onClick={onLogout}>Logout</NavLink>
                    </li>
                ) : (
                    <li>
                        <NavLink to="/login" className={({ isActive }) => isActive ? 'active' : ''}>Login</NavLink>
                    </li>
                )}
            </ul>
            <div className="search-container">
                <input type="text" placeholder="Search..." />
                <button type="submit">Search</button>
            </div>
        </div>
    );
}
