// src/Login.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import useNavigate
import './Login.css'; // Import the CSS file

// Add images for Google and Instagram
import googleIcon from './images/google-icon.png'; // Adjust path to where the image is located
import instagramIcon from './images/instagram-icon.png'; // Adjust path to where the image is located

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate(); // Initialize useNavigate

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle login logic here
        console.log('Email:', email);
        console.log('Password:', password);
        
        // Navigate to another route after login
        navigate('/dashboard'); // Replace '/dashboard' with your desired route
    };

    const handleGoogleLogin = () => {
        // Handle Google login logic here
        console.log("Login with Google");
        // Example of navigation after successful login
        navigate('/dashboard');
    };

    const handleInstagramLogin = () => {
        // Handle Instagram login logic here
        console.log("Login with Instagram");
        // Example of navigation after successful login
        navigate('/dashboard');
    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Login</button>
            </form>
            
            <p className="forgot-password">
                <Link to="/forgot-password">Forgot your password?</Link>
            </p>
            
            <div className="social-login">
                <button className="google-login" onClick={handleGoogleLogin}>
                    <img src={googleIcon} alt="Google" />
                    Login with Google
                </button>
                <button className="instagram-login" onClick={handleInstagramLogin}>
                    <img src={instagramIcon} alt="Instagram" />
                    Login with Instagram
                </button>
            </div>

            <p>
                Don't have an account?{' '}
                <Link to="/register">Register here</Link>
            </p>
        </div>
    );
};

export default Login;
